import React, { useState, useEffect } from 'react'
import axios from 'axios'
import { makeStyles } from '@material-ui/core/styles';

import Slide from '@material-ui/core/Slide';
import Dialog from '@material-ui/core/Dialog';
import IconButton from '@material-ui/core/IconButton';



const useStyles = makeStyles((theme) => ({
    appBar: {
        position: 'relative',
    },
    title: {
        marginLeft: theme.spacing(2),
        flex: 1,
    },
}));

const Transition = React.forwardRef(function Transition(props, ref) {
    return <Slide direction="up" ref={ref} {...props} />;
});


const CreateCompany = () => {

    const classes = useStyles();
    const [open, setOpen] = React.useState(false);

    const [name, setName] = useState([]);
    const [active, setActive] = useState('1')
    const [detail, setDetail] = useState({})
    const [id_company_detail, setId_company_detail] = useState("2");
    const [idCompany_detaill, setIdCompany_detaill] = useState({});
    const [id, setId] = useState(2)


    const [tempp, setTempp] = useState("")

    const [id_person, setId_person] = useState()
    const [usernames, setUsernames] = useState()
    const [id_company, setId_company] = useState("")
    // const [name, setName] = useState()
    const [id_policy_type, setId_policy] = useState(1)
    const [name_policy, setName_policy] = useState("")
    const [datail, setDatail] = useState("")
    const [numberreal, setNumberreal] = useState(1)
    const [create, setCreate] = useState("")
    const [id_message, setId_message] = useState(1)
    const [type, setType] = useState("")
    const [message, setMessage] = useState("")
    const [steeps, setSteeps] = useState(3) // para cuando inserte pase al siguiente paso 
    const [phone, setPhone] = useState([])
    const [fullname, setFullname] = useState([])




    const [palabra, setPalabra] = useState()

    const [busqueda, setBusqueda] = useState([]);
    const [se, setSe] = useState(true);
    const [comp, setComp] = useState([])
    const [compDetail, setCompDetail] = useState([])

    const [compp, setCompp] = useState([])

    const [show, setShow] = useState(true);

    useEffect(() => {

        getDataCompany()
        getDataTemp()
        const hoy = new Date()
        const fecha = hoy.getDate() + '-' + hoy.getMonth() + '-' + hoy.getFullYear();
        setDetail(fecha)
        setCreate(fecha)



        setInterval(() => {
            getDataCompany()
        }, 5000);

    }, [])

    const getDataCompany = async () => {
        const resultt = await axios.get(`http://localhost:3500/companies/`);
        setComp(resultt.data)
    }

    const getDataTemp = async () => {
        const resultt = await axios.get(`http://localhost:3500/temp/2`);
        const re = resultt.data[0]
        setId_person(re.id_person)
        setUsernames(re.usernames)
        setFullname(re.fullname)
        setPhone(re.phone)
        console.log("***************************************************************************")
        console.log(re)
        console.log("***************************************************************************")

    }

    const createCompany = async (e) => {
        e.preventDefault()
        const resultCreateCompany = await axios.post(`http://localhost:3500/companies/`, {
            detail_c: detail,
            active,
            id_company_detail,
            name
        })
        // getDataCompany()
        console.log(resultCreateCompany.data.status)
        capIdTemp()
    }

    const capIdTemp = async () => {
        const x = comp[0].id_company
        const ids = parseInt(x)
        const fi = comp.filter(item => item.id_company === ids)
        console.log("*****capIdTemp***********", fi[0], "*****************")
        setName(fi[0].name)
        setDetail(fi[0].detail_c)
        // insertTemp()
        // insertTemp()
        try {
            const id = 2;
            const resultP = await axios.post(`http://localhost:3500/temp/${id}`, {
                id_person, usernames, id_company: comp[0].id_company, name: fi[0].name, id_policy_type, name_policy, datail, numberreal, create, id_message, type, message, steeps: "3", phone, fullname
            })
            console.log(resultP.status)

        } catch (error) {
            console.log(error)
        }
    }

    const catchCompany = (e) => {
        e.preventDefault()
        const ids = parseInt(compp)
        alert(comp)
        const fi = comp.filter(item => item.id_company === ids)
        console.log(fi[0])
        setId_company(parseInt(fi[0].id_company))
        setName(fi[0].name)
        setDetail(fi[0].detail_c)
        insertTemp()
        insertTemp()
    }

    const cathCompanyC = (id) => {

        const ida = parseInt(id)
        const finddd = comp.find(item => item.id_company === ida)
        console.log("**********find*************")
        console.log(finddd)
        console.log("**********find*************")


        const insertTemp = async (e) => {
            try {
                const id = 2;
                const resultP = await axios.post(`http://localhost:3500/temp/${id}`, {
                    id_person, usernames, id_company, name, id_policy_type, name_policy, datail, numberreal, create, id_message, type, message, steeps: "3", phone, fullname
                })
                console.log(resultP.status)


            } catch (error) {
                console.log(error)
            }
        }

    }



    const insertTemp = async (e) => {
        console.log(id_person, usernames, id_company, name, id_policy_type, name_policy, datail, numberreal, create, id_message, type, message, steeps, phone, fullname
        )
        const id = 2;
        const resultP = await axios.post(`http://localhost:3500/temp/${id}`, {
            id_person, usernames, id_company, name, id_policy_type, name_policy, datail, numberreal, create, id_message, type, message, steeps: "3", phone, fullname
        })
        console.log(resultP.status)

    }




    return (
        <div>


            <div>


                <div className="row">
                    <div className="col-1"></div>
                    <div className="col-10">

                        <div>
                            <div className="row">



                                <div className="col-6">

                                    <h4>Crear Compañia</h4>
                                    <form onSubmit={createCompany}>

                                        <input name="name" type="text" class="form-control" placeholder={id_company} aria-label="C" aria-describedby="basic-addon2"
                                            onChange={(e) => setName(e.target.value)}
                                        />


                                        {
                                            se ? (
                                                <button type="submit" class="btn btn-primary btn-sm">Crear</button>

                                            ) : ("")
                                        }
                                    </form><br /><br />
                                </div>
                                <div className="col-6">
                                    <h4>Seleccione Compañia
                                    </h4>
                                    <form onSubmit={catchCompany}>

                                        <select class="form-select" aria-label="Default select example"
                                            // onChange={(e) => setCompp(e.target.value)}
                                            // onChange={(e) => alert(e.target.value)}
                                            onChange={(e) => cathCompanyC(e.target.value)}


                                        ><option>compañias </option>
                                            {
                                                comp.map((item, index) =>
                                                    <option key={index} value={item.id_company} > id -companía {item.id_company} --{item.name}</option>

                                                )
                                            }
                                        </select>


                                        <div class="d-grid gap-2">
                                            {

                                                se ? (
                                                    <button type="submit" className=" btn btn-primary sm-block">Seleccionar</button>

                                                ) : ("")
                                            }
                                        </div>
                                    </form>

                                </div>
                            </div>




                        </div>
                    </div>
                </div>



            </div>
        </div>
    )
}

export default CreateCompany
