import React, { useState } from 'react'



import clsx from 'clsx';
import { makeStyles, useTheme } from '@material-ui/core/styles';
import Drawer from '@material-ui/core/Drawer';
import CssBaseline from '@material-ui/core/CssBaseline';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import List from '@material-ui/core/List';
import Typography from '@material-ui/core/Typography';
import Divider from '@material-ui/core/Divider';
import IconButton from '@material-ui/core/IconButton';
import MenuIcon from '@material-ui/icons/Menu';
import ChevronLeftIcon from '@material-ui/icons/ChevronLeft';
import ChevronRightIcon from '@material-ui/icons/ChevronRight';
import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemText from '@material-ui/core/ListItemText';
import InboxIcon from '@material-ui/icons/MoveToInbox';
import MailIcon from '@material-ui/icons/Mail';


import { withStyles } from '@material-ui/core/styles';
import MuiAccordion from '@material-ui/core/Accordion';
import MuiAccordionSummary from '@material-ui/core/AccordionSummary';
import MuiAccordionDetails from '@material-ui/core/AccordionDetails';
import Accordion from '@material-ui/core/Accordion'
import axios from 'axios';


const useStyles = makeStyles((theme) => ({
    root: {
        display: 'flex',
        border: '1px solid rgba(0, 0, 0, .125)',
        boxShadow: 'none',

    },
    '&:before': {
        display: 'none',
    },
    '&$expanded': {
        margin: 'auto',
    },
    appBar: {
        transition: theme.transitions.create(['margin', 'width'], {
            easing: theme.transitions.easing.sharp,
            duration: theme.transitions.duration.leavingScreen,
        }),
    },

    contentShift: {
        transition: theme.transitions.create('margin', {
            easing: theme.transitions.easing.easeOut,
            duration: theme.transitions.duration.enteringScreen,
        }),
        marginLeft: 0,
    },

}));

const AccordionSummary = withStyles({
    root: {
        backgroundColor: 'rgba(0, 0, 0, .03)',
        borderBottom: '1px solid rgba(0, 0, 0, .125)',
        marginBottom: -1,
        minHeight: 56,
        '&$expanded': {
            minHeight: 56,
        },
    },
    content: {
        '&$expanded': {
            margin: '12px 0',
        },
    },
    expanded: {},
})(MuiAccordionSummary);

const AccordionDetails = withStyles((theme) => ({
    root: {
        padding: theme.spacing(2),
    },
}))(MuiAccordionDetails);




const SiderBar = () => {



    //configuración
    const [modoGuiado, setModoGuiado] = useState(false)
    const [dataTemp, setDataTemp] = useState([])
    const [def, setDef] = useState(false)

    const [bar, setBar] = useState("0")
    const [u, setU] = useState('0')
    const [tempSteep, setDataTempSteep] = useState(false)

    //seteo 
    const [usuario, setUsuario] = useState(true)
    const [companies, setCompanies] = useState(false)
    const [type, setType] = useState(false)
    const [fdata, setFdata] = useState(false)
    const [nPoliza, setNpoliza] = useState(false)
    const [message, setMessage] = useState(false)
    const [fin, setFin] = useState(false)
    const [a, setA] = useState("")
    const [dataPolicy, setDataPolicy] = useState([])
    const [datafiltradafechaPolicity, setDatafiltradafechaPolicity] = useState([])

    const [dHoy, setDhoy] = useState('')
    const [datafiltradafechaPolicityHoy, setDatafiltradafechaPolicityHoy] = useState("")



    const classes = useStyles();
    const theme = useTheme();
    const [open, setOpen] = React.useState(false);
    const handleDrawerOpen = () => {
        setOpen(true);
    };
    const handleDrawerClose = () => {
        setOpen(false);
    };





    const getDataPolicy = async (e) => {

        const result = await axios.get(`http://localhost:3500/policyss`);
        console.log(result.data)
        setDataPolicy(result.data)
        clock(result.data)
        clockHoy(result.data)

    }
    const clock = (response) => {
        const s = response;
        const n = [];
        const nn = [...n];

        const sumardias = (fecha, dias) => {
            fecha.setDate(fecha.getDate() + dias)
            nn.push(fecha)
        }
        const d = new Date();
        //aca se setea cuantos días 
        const f = sumardias(d, +2);
        const diaNumeroHoy = new Date(nn).getDate();

        const resultado = s.filter((item) => {
            // const resultadofecha = new Date(item.create).getDate();

            if (new Date(item.create).getDate() === diaNumeroHoy) {
                return item;
            }
        }
        );
        console.log(resultado)
        setDatafiltradafechaPolicity(resultado)

    }
    const clockHoy = (response) => {
        const s = response;


        const hoy = new Date()
        const fecha = hoy.getDate() + '-' + hoy.getMonth() + '-' + hoy.getFullYear();
        setDhoy(fecha)



        console.log(s[0])



    }



    const [expanded, setExpanded] = React.useState('panel1');

    const handleChange = (panel) => (event, newExpanded) => {
        setExpanded(newExpanded ? panel : false);
    };







    return (
        <div>
            <div className="container">
                <p>esto</p>

                <List className="btn btn-primary" id="nico" >
                    <Accordion square expanded={expanded === 'panel2'} onChange={handleChange('panel2')} >
                        <AccordionSummary aria-controls="panel2d-content" id="panel2d-header">
                            <Typography>Poliza Temporal</Typography>
                        </AccordionSummary>
                        <AccordionDetails >
                            <Typography className="btn btn-primary" >
                                <SiderBar />
                            </Typography>
                        </AccordionDetails>
                    </Accordion>
                    <Accordion square expanded={expanded === 'panel3'} onChange={handleChange('panel3')} >
                        <AccordionSummary aria-controls="panel3d-content" id="panel3d-header">
                            <Typography>Mensajes para hoy </Typography>
                        </AccordionSummary>
                        <AccordionDetails>
                            <Typography className="btn btn-primary">
                                <p>Hoy se le enviará mensaje de vencimiento a las siguientes personas</p>
                                <br />
                                {
                                    datafiltradafechaPolicity.map((item) =>
                                        <ul key={item.id_policy}>

                                            <li>
                                                {item.fullname} - {item.numberreal}
                                            </li>
                                        </ul>


                                    )
                                }

                            </Typography>
                        </AccordionDetails>
                    </Accordion>
                    {/* <Accordion square expanded={expanded === 'panel4'} onChange={handleChange('panel4')}>
                            <AccordionSummary aria-controls="panel3d-content" id="panel3d-header">
                                <Typography>Polizas Creadas Hoy </Typography>
                            </AccordionSummary>
                            <AccordionDetails>
                                <Typography>
                                    <p>Hoy se le enviará mensaje de vencimiento a las siguientes personas</p>
                                    <br />
                                    {
                                        datafiltradafechaPolicityHoy.map((item) =>
                                        <ul key={item.id_policy}>

                                            <li>
                                                {item.fullname} - {item.numberreal}
                                            </li>
                                        </ul>


                                    )
                                    }

                                    <div className="btn btn-primary" onClick={clockHoy}>prueba</div>
                                </Typography>
                            </AccordionDetails>
                        </Accordion> */}
                </List>
                <Divider />
                <List>

                    {/* <ListItem >
                            <Typography>
                                Aca va las lapizas asignadas a este usuario
                                <div className="card">
                                    hola
                                </div>
                            </Typography>
                        </ListItem>
                        <Divider />
                        <Divider />
                        <ListItem >
                            <Typography>
                                Aca va las lapizas asignadas a este usuario
                                <div className="card">
                                    hola
                                </div>
                            </Typography>
                        </ListItem> */}

                </List>





            </div>
        </div>
    )
}

export default SiderBar
