        e.preventDefault()

    }

    const busquedas = async (e) => {
        e.preventDefault();
        // console.log(getD) 

        const filtrar = await getD.filter(item => (busqueda === item.usernames || busqueda === item.fullname || busqueda === item.id_person || busqueda === item.email || busqueda === item.phone))
        console.log(filtrar)
        setFilt(filtrar)
        setfiltv(true)

    }


