import React, { useState, useEffect } from 'react'

import User from './users/User';
import Company from './companies/Company';
import Insurance from './insurance/Insurance';
import Maturities from './maturities/Maturities'
import Policy from './policies/Policy';
import Messages from './messages/Messages'
import Finish from './finish/Finish'




import { makeStyles } from '@material-ui/core/styles';
import Stepper from '@material-ui/core/Stepper';
import Step from '@material-ui/core/Step';
import StepLabel from '@material-ui/core/StepLabel';
import StepContent from '@material-ui/core/StepContent';
import Button from '@material-ui/core/Button';
import Paper from '@material-ui/core/Paper';
import Typography from '@material-ui/core/Typography';
import axios from 'axios';


const useStyles = makeStyles((theme) => ({
    root: {
        width: '100%',
    },
    button: {
        marginTop: theme.spacing(1),
        marginRight: theme.spacing(1),
    },
    actionsContainer: {
        marginBottom: theme.spacing(2),
    },
    actionsContainer: {
        marginBottom: theme.spacing(3),
    },
    resetContainer: {
        padding: theme.spacing(4),
    },
}));


const Stepptwo = () => {


    const [pe, setPe] = useState(`<User/>`)
    const [paso, setPaso] = useState("false")
    const [mostrar, setMostrar] = useState(false)
    const [bar, setBar] = useState("0")
    const [dataTemp, setDataTemp] = useState([])
    const [tempSteep, setDataTempSteep] = useState(false)
    const [def, setDef] = useState(false)





    const getDataTemp = async (e) => {


        const result = await axios.get(`http://localhost:3500/temp`);
        console.log(result.data)

        setDataTemp(result.data)

        if (result.data === '' || result.data === "null") {
            alert("no tiene elementos")
            console.log(result.data)
        } else {
            console.log(result.data[0].steeps)
            setDataTempSteep(result.data[0].steeps)
            setDef(true)
            config()

        }



    }



    function getSteps() {
        return ['Cliente', 'Compañia', 'Detalle Poliza', 'Fecha', 'Numero de Poliza', 'Mensaje', 'Inserción terminada'];
    }
    function getStepContent(step) {
        switch (step) {
            case 0:
                return `For each ad campaign that you create, you can control how much
                    you're willing to spend on clicks and conversions, which networks
                    and geographical locations you want your ads to show on, and more.`;
            case 1:
                return 'An ad group contains one or more ads which target a shared set of keywords.';
            case 2:
                return `Try out different ad text to see what brings in the most customers,
                    and learn how to enhance your ads using features like ad extensions.
                    If you run into any problems with your ads, find out how to tell if
                    they're running and how to resolve approval issues.`;
            case 3:

                return `${pe}`;
            default:
                return 'Unknown step';
        }
    }

    const classes = useStyles();
    const [activeStep, setActiveStep] = useState(0);
    const steps = getSteps();

    const handleNext = () => {
        setActiveStep((prevActiveStep) => prevActiveStep + 1);
    };

    const handleBack = () => {
        setActiveStep((prevActiveStep) => prevActiveStep - 1);
    };

    const handleReset = () => {
        setActiveStep(0);
    };
    return (
        <div>

            <div className="row">

                <div className="col-1">

                </div>
                <div className="col-8">


                    <div className={classes.root}>
                        <Stepper activeStep={activeStep} orientation="vertical">
                            {steps.map((label, index) => (
                                <Step key={label}>
                                    <StepLabel>{label}</StepLabel>
                                    <StepContent>
                                        {label === 'Cliente' ? (<User />) : ("")}
                                        {label === 'Compañia' ? (<Company />) : ("")}
                                        {label === 'Detalle Poliza' ? (<Insurance />) : ("")}
                                        {label === 'Fecha' ? (<Maturities />) : ("")}
                                        {label === 'Numero de Poliza' ? (<Policy />) : ("")}
                                        {label === 'Mensaje' ? (<Messages />) : ("")}
                                        {label === 'Inserción terminada' ? (<Finish />) : ("")}




                                        {/* 
                                        <Typography>

                                            {
                                                getStepContent(index)




                                            }</Typography> */}

                                        <div className={classes.actionsContainer}>
                                            <div>
                                                <Button
                                                    disabled={activeStep === 0}
                                                    onClick={handleBack}
                                                    className={classes.button}
                                                >
                                                    Back
                                                </Button>
                                                <Button
                                                    variant="contained"
                                                    color="primary"
                                                    onClick={handleNext}
                                                    className={classes.button}
                                                >
                                                    {activeStep === steps.length - 1 ? 'Finish' : 'Next'}
                                                </Button>
                                            </div>
                                        </div>
                                    </StepContent>
                                </Step>
                            ))}
                        </Stepper>
                        {activeStep === steps.length && (
                            <Paper square elevation={0} className={classes.resetContainer}>
                                <Typography>All steps completed - you&apos;re finished</Typography>
                                <Button onClick={handleReset} className={classes.button}>
                                    Reset
                                </Button>
                            </Paper>
                        )}
                    </div>

                </div>
                <div className="col-3">
                    <div className="flotante">



                        <div className="rb">
                            <div className="container">

                                <h5 class="card-title">Poliza</h5>
                                <div id="donut" data-role="donut" data-value={bar} class="mx-auto"></div>


                            </div>

                            {
                                mostrar ? (

                                    <div className="container">
                                        <span>
                                            <h5>
                                                person

                                            </h5>

                                            id: {dataTemp[0].id_person} -  Nombre: {dataTemp[0].usernames} Nombre completo: {dataTemp[0].fullname} Teléfono:{dataTemp[0].phone}<br />

                                        </span>

                                        <span>

                                            <h5>

                                                company

                                            </h5>
                                            id_compañia: {dataTemp[0].id_company} - Nombre de la compañia:   {dataTemp[0].name}  <br />

                                        </span>
                                        <span>
                                            <h5>

                                                tipo de poliza

                                            </h5>
                                            Tipo de poliza: {dataTemp[0].id_policy_type} - nombre de la poliza {dataTemp[0].name_policy} - {dataTemp[0].detail} <br />

                                        </span>
                                        <span>

                                            <h5>
                                                numero de Poliza

                                            </h5>
                                            Número de poliza Real: {dataTemp[0].numerreal} - creado: {dataTemp[0].create} <br />

                                        </span>

                                        <span>
                                            <h5>Mensajes</h5>
                                            id_Mensaje: {dataTemp[0].id_message} - typo de mensage: {dataTemp[0].type} - {dataTemp[0].message} <br />

                                        </span>
                                    </div>





                                ) : ("")

                            }





                            <ul >

                                {/*                                      


                                        <span>
                                            <h5>
                                                person

                                            </h5>

                                            id: {dataTemp[0].id_person} -  Nombre: {dataTemp[0].usernames} Nombre completo: {dataTemp[0].fullname} Teléfono:{dataTemp[0].phone}<br />

                                        </span>

                                        <span>

                                            <h5>

                                                company

                                            </h5>
                                            id_compañia: {dataTemp[0].id_company} - Nombre de la compañia:   {dataTemp[0].name}  <br />

                                        </span>
                                        <span>
                                            <h5>

                                                tipo de poliza

                                            </h5>
                                            Tipo de poliza: {dataTemp[0].id_policy_type} - nombre de la poliza {dataTemp[0].name_policy} - {dataTemp[0].detail} <br />

                                        </span>
                                        <span>

                                            <h5>
                                                numero de Poliza

                                            </h5>
                                            Número de poliza Real: {dataTemp[0].numerreal} - creado: {dataTemp[0].create} <br />

                                        </span>

                                        <span>
                                            <h5>Mensajes</h5>
                                            id_Mensaje: {dataTemp[0].id_message} - typo de mensage: {dataTemp[0].type} - {dataTemp[0].message} <br />

                                        </span>
 */}


                            </ul>





                            <button class="btn btn-primary" onClick={getDataTemp}>Actualizar</button>
                        </div>



                    </div>
                </div>
            </div>







        </div>
    )
}

export default Stepptwo
