import React, { useState, useEffect } from 'react'

import User from './users/User';
import Company from './companies/Company';
import Insurance from './insurance/Insurance';
import Maturities from './maturities/Maturities'
import Policy from './policies/Policy';
import Messages from './messages/Messages'
import Finish from './finish/Finish'




import { makeStyles } from '@material-ui/core/styles';
import Stepper from '@material-ui/core/Stepper';
import Step from '@material-ui/core/Step';
import StepLabel from '@material-ui/core/StepLabel';
import StepContent from '@material-ui/core/StepContent';
import Button from '@material-ui/core/Button';
import Paper from '@material-ui/core/Paper';
import Typography from '@material-ui/core/Typography';


const useStyles = makeStyles((theme) => ({
    root: {
        width: '100%',
    },
    button: {
        marginTop: theme.spacing(1),
        marginRight: theme.spacing(1),
    },
    actionsContainer: {
        marginBottom: theme.spacing(2),
    },
    actionsContainer: {
        marginBottom: theme.spacing(3),
    },
    resetContainer: {
        padding: theme.spacing(4),
    },
}));


const Stepptwo = () => {


    const [pe, setPe] = useState(`<User/>`)
    const [paso, setPaso] = useState("false")


    function getSteps() {
        return ['Seleccionar Usuario', 'Create an ad group', 'Create an ad', 'la la la '];
    }
    function getStepContent(step) {
        switch (step) {
            case 0:
                return `For each ad campaign that you create, you can control how much
                    you're willing to spend on clicks and conversions, which networks
                    and geographical locations you want your ads to show on, and more.`;
            case 1:
                return 'An ad group contains one or more ads which target a shared set of keywords.';
            case 2:
                return `Try out different ad text to see what brings in the most customers,
                    and learn how to enhance your ads using features like ad extensions.
                    If you run into any problems with your ads, find out how to tell if
                    they're running and how to resolve approval issues.`;
            case 3:

                return `${pe}`;
            default:
                return 'Unknown step';
        }
    }

    const classes = useStyles();
    const [activeStep, setActiveStep] = useState(0);
    const steps = getSteps();

    const handleNext = () => {
        setActiveStep((prevActiveStep) => prevActiveStep + 1);
    };

    const handleBack = () => {
        setActiveStep((prevActiveStep) => prevActiveStep - 1);
    };

    const handleReset = () => {
        setActiveStep(0);
    };
    return (
        <div>

            <div className="row">

                <div className="col-2">

                </div>
                <div className="col-10">


                    <div className={classes.root}>
                        <Stepper activeStep={activeStep} orientation="vertical">
                            {steps.map((label, index) => (
                                <Step key={label}>
                                    <StepLabel>{label}</StepLabel>
                                    <StepContent>
                                        <Typography>

                                            {
                                                getStepContent(index)




                                            }</Typography>

                                        <div className={classes.actionsContainer}>
                                            <div>
                                                <Button
                                                    disabled={activeStep === 0}
                                                    onClick={handleBack}
                                                    className={classes.button}
                                                >
                                                    Back
                                                </Button>
                                                <Button
                                                    variant="contained"
                                                    color="primary"
                                                    onClick={handleNext}
                                                    className={classes.button}
                                                >
                                                    {activeStep === steps.length - 1 ? 'Finish' : 'Next'}
                                                </Button>
                                            </div>
                                        </div>
                                    </StepContent>
                                </Step>
                            ))}
                        </Stepper>
                        {activeStep === steps.length && (
                            <Paper square elevation={0} className={classes.resetContainer}>
                                <Typography>All steps completed - you&apos;re finished</Typography>
                                <Button onClick={handleReset} className={classes.button}>
                                    Reset
                                </Button>
                            </Paper>
                        )}
                    </div>

                </div>
            </div>





            <div class="row mt-1">
                <div class="col-md-12">

                    <ul class="stepper stepper-vertical">

                        <li class="completed">
                            <a href="#!">
                                <span class="circle">1</span>
                                <span class="label">First step</span>
                            </a>
                        </li>

                        <li class="active">

                            <a href="#!">
                                <span class="circle">2</span>
                                <span class="label">Second step</span>
                            </a>

                            <div class="step-content grey lighten-3">
                                <p>Lorem ipsum dolor sit amet, consectetur adipisicing elit. Esse cupiditate voluptate facere
                                    iusto
                                    quaerat
                                    vitae excepturi, accusantium ut aliquam repellat atque nesciunt nostrum similique. Inventore
                                    nostrum
                                    ut,
                                    nobis porro sapiente.</p>
                                <p>Lorem ipsum dolor sit amet, consectetur adipisicing elit. Dolore error excepturi veniam nemo
                                    repellendus, distinctio soluta vitae at sit saepe. Optio eaque quia excepturi adipisci pariatur
                                    totam,
                                    atque odit fugiat.</p>
                                <p>Deserunt voluptatem illum quae nisi soluta eum perferendis nesciunt asperiores tempore saepe
                                    reiciendis,
                                    vero quod a dolor corporis natus qui magni quas fuga rem excepturi laboriosam. Quisquam
                                    expedita ab
                                    fugiat.</p>
                            </div>
                        </li>

                        <li class="warning">
                            <a href="#!">
                                <span class="circle"><i class="fas fa-exclamation"></i></span>
                                <span class="label">Third step</span>
                            </a>
                        </li>

                        <li>
                            <a href="#!">
                                <span class="circle">4</span>
                                <span class="label">Fourth step</span>
                            </a>
                        </li>

                    </ul>

                </div>
            </div>

            <div class="row mt-1">
                <div class="col-md-12 text-right">
                    <button class="btn btn-flat btn-sm">Cancel</button>
                    <button class="btn btn-primary btn-sm">Next</button>
                </div>
            </div>


        </div>
    )
}

export default Stepptwo
