import { set } from 'react-hook-form';


const Pre = () => {
    const [me, setMe] = useState("")
    const [reM, setReM] = useState("")
    const [reE, setReE] = useState("")


    useEffect(() => {
        // getData()
        messagess();
        setTimeout(() => {
            setInterval(() => {
                setTimeout(() => {

                    email();
