        }, 10000);
        setTimeout(() => {
            setInterval(() => {
                setTimeout(() => {
                    alert()
                    if (reM === reE) {
                        alert("Correo y Mensajes Enviados")
                    }
                    }, 1000);
                }, 14000);
            }, 15000);

    }, [])
    const messagess = async () => {
        const result = await axios.get(`http://localhost:3500/whatsapp/connects`);
        console.log(result.data.status);
        if (result.data.status === 200) {
            setReM(true)
        }
    }
    const email = async () => {
        const result = await axios.post(`http://localhost:3500/send-emaill`);
        console.log(result.data.status)
        if (result.data.status === 200) {
            setReE(true)
        }
    }
    return (
        <div>
            <div class="center-block">

                <div className="box">

                    <br />


                    <div class="d-flex justify-content-center">
                        <div class="jumbotron">
                            <h1 class="title">Sistema Seguros   ncV0.0.2</h1><br />
                            <h6>
                                {/* <CircularProgress disableShrink fontSize="large" />
                                v0.0.1 

                            <CircularProgress disableShrink fontSize="large" />
                                 */}
                            </h6>
