                                <p>Bienvenido al sistema de seguros</p>

                                <br />
                                <div data-role="cube"
                                    data-color="bg-cyan bd-darkCyan"
                                    data-flash-color="#aa00ff"></div>
                                <br /><br />

                            </h5>

                        </div>

                    </div>



                </div>
            </div>
        </div>
    )
}

export default Pre
