import axios from 'axios'
import React, { useState, useEffect } from 'react'

const CreatePolicy = () => {

    const [numero, setNumero] = useState("")


    const [si, setSi] = useState(false)

    const [id_person, setId_person] = useState()
    const [usernames, setUsernames] = useState()
    const [id_company, setId_company] = useState("")
    const [name, setName] = useState()
    const [id_policy_type, setId_policy] = useState("")
    const [name_policy, setName_policy] = useState("")
    const [datail, setDatail] = useState("")
    const [numberreal, setNumberreal] = useState("")
    const [create, setCreate] = useState("")
    const [id_message, setId_message] = useState(1)
    const [type, setType] = useState("algo")
    const [message, setMessage] = useState("en caso de que no llegue a mensaje")
    const [phone, setPhone] = useState([])
    const [fullname, setFullname] = useState([])
    const [steeps, setSteeps] = useState(6)

    useEffect(() => {

        const hoy = new Date()
        const fecha = hoy.getDate() + '-' + hoy.getMonth() + '-' + hoy.getFullYear();
        getDataTemp();

    }, [])



    const getDataTemp = async () => {
        const resultt = await axios.get(`http://localhost:3500/temp/2`);
        const re = resultt.data[0]
        console.log(re)

        setId_person(re.id_person);
        setUsernames(re.usernames);
        setFullname(re.fullname);
        setPhone(re.phone);
        setId_company(re.id_company);
        setName(re.name);
        setId_policy(re.id_policy_type);
        setName_policy(re.name_policy)
        setName(re.name);
        setDatail(re.datail)
        setCreate(re.create)
    }

    const crearNumeroPöliza = async (e) => {
        e.preventDefault()


        console.log("enviado")
        console.log("id_person, usernames, id_company, name, id_policy_type, name_policy, datail, numberreal, create, id_message, type, message, steeps, phone, fullname")

        console.log(id_person, usernames, id_company, name, id_policy_type, name_policy, datail, numberreal, create, id_message, type, message, steeps, phone, fullname)
        insertTemp()
    }

    const insertTemp = async () => {
        const id = 2;
        setSteeps(6)

        const resultP = await axios.post(`http://localhost:3500/temp/${id}`, {
            id_person, usernames, id_company, name, id_policy_type, name_policy, datail, numberreal, create, id_message, type, message, steeps, phone, fullname
        })
        console.log(resultP.status)
        if (resultP.status === 200) {
            insert()
        } else {
            console.log("se produjo un error");
        }
    }
    const insert = async () => {
        console.log(`id policy_type:${id_policy_type}, datail:${datail}, id_company:${id_company}, create:${create}, numberreal:${numberreal}`)
        try {



            const resultP = await axios.post(`http://localhost:3500/policys/`, {
                id_policy_typess: id_policy_type, detail: datail, active: "True", id_person, id_company, create, numberreal:numberreal
            })
            if (resultP.status === 200) {
                console.log(resultP.status)
            }
        } catch (error) {
            console.log(error)
        }

    }


    return (
        <div>
            <h5>
                Ingrese el número de poliza real
            </h5>
            <form onSubmit={crearNumeroPöliza}>

                <input name="numberreal" type="text" class="form-control" placeholder={`ingrese numero de poliza`} aria-label="Recipient's username" aria-describedby="basic-addon2"
                    onChange={(e) => setNumberreal(e.target.value)}
                />


                <button type="submit" class="btn btn-primary btn-sm">Crear</button>
            </form><br /><br />
        </div>
    )
}

export default CreatePolicy
