import React from 'react';
import { useEffect } from 'react';
import { useState } from 'react';
import axios from 'axios';

import { makeStyles } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';

// import DatePicker from "react-datepicker";

// import "react-datepicker/dist/react-datepicker.css";

// import DatePicker from 'react-date-picker';


const useStyles = makeStyles(theme => ({
    container: {
        display: 'flex',
        flexWrap: 'wrap',
    },
    textField: {
        marginLeft: theme.spacing(1),
        marginRight: theme.spacing(1),
        width: 200,
    },
}));




const CreateMaturities = () => {
    const classes = useStyles();

    const [fecha, setFecha] = useState("")

    const [value, updateValue] = useState(new Date());

    const onChange = (date) => {
        updateValue(date);
    }



    const [si, setSi] = useState(false)


    const [id_person, setId_person] = useState()
    const [usernames, setUsernames] = useState()
    const [id_company, setId_company] = useState("")
    const [name, setName] = useState()
    const [id_policy_type, setId_policy] = useState()
    const [name_policy, setName_policy] = useState("")
    const [datail, setDatail] = useState("")
    const [numberreal, setNumberreal] = useState(1)
    const [create, setCreate] = useState("")
    const [id_message, setId_message] = useState(1)
    const [type, setType] = useState("")
    const [message, setMessage] = useState("")
    const [steeps, setSteeps] = useState(5) // para cuando inserte pase al siguiente paso 
    const [phone, setPhone] = useState([])
    const [fullname, setFullname] = useState([])




    // const [value, setValue] = React.useState(new Date());
    useEffect(() => {
        const hoy = new Date()
        const x = hoy.getDate() + '-' + hoy.getMonth() + '-' + hoy.getFullYear();
        setFecha(x)
        getDataTemp()
        // getDataTypeInsurance()
    }, [])




    const getDataTemp = async () => {
        const resultt = await axios.get(`http://localhost:3500/temp/2`);
        const re = resultt.data[0]
        console.log(re)

        setId_person(re.id_person);
        setUsernames(re.usernames);
        setFullname(re.fullname);
        setPhone(re.phone);
        setId_company(re.id_company);
        setName(re.name);
        setId_policy(re.id_policy_type);
        setName_policy(re.name_policy);
        setDatail(re.datail);
        setNumberreal(re.numberreal);
        setCreate(re.create)
    }

    // const getDataTypeInsurance = async () => {
    //     const resultt = await axios.get(`http://localhost:3500/policysType/`);
    //     const re = resultt.data[0].id_policy_type
    //     console.log(re,"esto viene de tipo de seguro")
    // }



    const crearFechadeCreaciono = (e) => {
        e.preventDefault()
        // console.log("*****************************************************************")
        console.log(create, "**********************************************************")
        // console.log("*****************************************************************")

        // console.log(id_person, usernames, id_company, name, id_policy_type, name_policy, datail, numberreal, create, id_message, type, message, steeps, phone, fullname)
        // insert()
        insertTemp()
    }


    const insertTemp = async () => {

        const id = 2;
        const resultP = await axios.post(`http://localhost:3500/temp/${id}`, {
            id_person, usernames, id_company, name, id_policy_type, name_policy, datail, numberreal, create, id_message, type, message, steeps, phone, fullname
        })
        alert(`${create} con un valor ${resultP.status}`)
    }



    return (
        <div>

            <form className={classes.container} noValidate>
                <TextField
                    id="date"
                    label="Birthday"
                    type="date"
                    defaultValue= {fecha}
                    className={classes.textField}
                    InputLabelProps={{
                        shrink: true,
                    }}
                />
            </form>





            <br />
            <h4>Ingrese fecha de CreaciÃ³n</h4>
            <form onSubmit={crearFechadeCreaciono}>
                {/* <DatePicker
                    selected={date}
                    onSelect={handleDateSelect} //when day is clicked
                    onChange={handleDateChange} //only when value has changed
                /> */}
                {/* <label >CompaÃ±ia</label> */}
                <input name="name" type="text" class="form-control" placeholder={`ingrese fecha de creaciÃ³n ej: ${fecha}`} aria-label="ingrese fecha de creaciÃ³n" aria-describedby="basic-addon2"
                    onChange={(e) => setCreate(e.target.value)}
                />



                <button type="submit" class="btn btn-primary btn-sm">Crear</button>
            </form><br /><br />

        </div>
    )
}

export default CreateMaturities
