    const getData = async () => {

        const result = await axios.get(`http://localhost:3500/user/`);
        const x = result.data
        console.log(x)

        // setBorrar(x)
        filtroUsuarioDesafectados(x)
    }

    const filtroUsuarioDesafectados = (x) => {
        console.log(x)
        const fi = x.filter(item => item.active === "true"

